sources.scss\\main.scss:
* {
  margin: 0;
  padding: 0;
}


li {
  list-style: none;
}

a {
  text-decoration: none;
  color: black;
}

body {
  font-family: "Noto Sans KR", sans-serif;
}

.wrap {
  // width: 1420px;
  // margin: 0 auto;
}

/* font-family: 'Noto Sans KR', sans-serif;
  font-family: 'Noto Serif', serif;
  font-family: 'Poppins', sans-serif; */
  .quick {
    width: 50px;
    height: 100px;
    position: fixed;
    right: 0;
    bottom: 50px;
    z-index: 1100;
  
    .scroll-btn {
      cursor: pointer;
      opacity: 0;
      width: 45px;
      height: 45px;
      background-image: url(../images/top.png);
      border-radius: 50%;
      transition: opacity 0.5s, transform 0.5s;
    }
  
    .scroll-btn.show {
      opacity: 1;
      transition: opacity 1s, transform 1s;
    }
  }

.hidden_history {
  display: none;
}

.shopping_history {

  padding: 40px 20px;
  position: fixed;
  right: 0;
  top: 0;
  z-index: 1000;
  width: 480px;
  height: 100%;
  background-color: #fff;
  border: 1px solid #eee;
  box-sizing: border-box;

  .history_top {
    display: flex;
    margin-bottom: 40px;
    justify-content: space-between;
    align-items: center;

    h1 {
      font-size: 14px;
      font-weight: bold;
    }

    .history_close_btn {
      width: 30px;
      height: 30px;

      &:hover {
        cursor: pointer;
      }

      i {
        font-family: "Font Awesome 5 Free";
        font-weight: 900;
      }
    }
  }

  h2 {
    font-size: 13px;
    margin-bottom: 40px;
  }

  .history_middle_ex {
    height: 620px;
    margin-bottom: 30px;

    .history_middle {
      display: flex;
      justify-content: space-between;
      width: 440px;
      height: 120px;
      margin-bottom: 20px;

      .history_middle_info {
        width: 420px;

        a {
          display: flex;

          .history_middle_image {
            width: 100px;
            height: 120px;
            margin-right: 20px;

            img {
              width: 100%;
              height: 100%;
            }
          }

          .history_middle_text {
            display: flex;
            justify-content: space-evenly;
            flex-direction: column;
          }
        }

      }

      .recent_close_btn {
        width: 30px;
        height: 30px;

        i {
          font-family: "Font Awesome 5 Free";
          font-weight: 900;
        }

        &:hover {
          cursor: pointer;
        }
      }
    }
  }

  .history_bottom {
    position: absolute;
    bottom: 45px;
    right: 20px;

    .delete_all {
      width: 100px;
      height: 40px;
      float: right;
      text-align: center;
      line-height: 40px;
      font-size: 13px;
      background-color: #f8f8f8;

      &:hover {
        background-color: #eee;
        cursor: pointer;
      }
    }
  }
}

.header_ex {
  width: 100%;

  .header {
    width: 1420px;
    margin: 0 auto;
    overflow: hidden;
    justify-content: space-around;
    display: flex;
    height: 130px;
    line-height: 130px;

    .logo {
      width: 220px;
      text-align: center;
      padding-top: 20px;
      box-sizing: border-box;

      img {
        width: 80px;
        height: 80px;
      }
    }

    .gnb {
      width: 740px;
      position: relative;

      ul {
        li {
          padding: 0 30px;
          box-sizing: border-box;

          &:hover {
            font-weight: bold;
            box-sizing: border-box;
            cursor: pointer;
          }

          a {
            display: block;
          }

        }
      }

      .shop {
        display: flex;
        justify-content: space-around;
      }

    }

    .hidden {
      display: none;

    }

    .shop_lnb {
      z-index: 100;
      width: 435px;
      height: 820px;
      background-color: #fff;
      position: absolute;
      top: 130px;
      left: 0;

      ul {
        li {
          //padding-left: 60px;
          height: 68px;
          line-height: 68px;
          box-sizing: border-box;

          a {
            padding-left: 60px;
            display: block;

            &:hover {
              font-weight: bold;
              background-color: #efe8da;
            }
          }

          &:first-child {
            display: flex;
            padding-left: 0;
            height: 72px;
            line-height: 72px;
            text-align: center;
            box-sizing: border-box;

            a {
              padding: 0;
              display: block;
              width: calc(435px / 2);

              &:hover {
                font-weight: bold;
                background-color: #efe8da;
              }

            }

          }
        }
      }
    }


  }

  .header_right {
    display: flex;
    justify-content: space-around;
    width: 430px;

    .search {
      position: relative;

      input {
        height: 25px;
        border: none;
        border-radius: 12px;
        background-color: #fd8234;
      }

      a {
        position: absolute;
        right: 7px;
        top: 3px;
      }
    }
  }


}

.visual_main.swiper {
  width: 1420px;
  height: 820px;
  margin-bottom: 75px;

  .swiper-slide {
    text-align: center;
    font-size: 18px;
    background: #fff;
    display: flex;
    justify-content: center;
    align-items: center;
    height: calc(100% - 130px);
  }

  .swiper-slide img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .swiper-pagination-progressbar.swiper-pagination-horizontal {
    width: 100%;
    height: 3px;
    left: 0;
    top: 700px;
    background-color: #e6e6e6;
  }

  .swiper-pagination-progressbar .swiper-pagination-progressbar-fill {
    background-color: #fd8234;
  }

  .swiper-button-next,
  .swiper-rtl .swiper-button-prev,
  .swiper-button-prev,
  .swiper-rtl .swiper-button-next {
    color: #e6e6e6;
  }
}

.new.swiper {
  width: 1420px;
  margin: 0 auto;
  // height: 100px;
  position: relative;
