  .swiper-wrapper {
    // position: relative;
    margin-bottom: 70px;

    .swiper-slide {
      position: relative;
      width: 360px;
      // background-color: orange;
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 24px;
      font-weight: bold;

      .swiper-slide-visible {
        .swiper-slide-next {
          transform: translate3d(-55px, -10px, -290px) rotateX(0deg) rotateY(0deg) scale(1);
        }

        .swiper-slide-prev {
          transform: translate3d(55px, -10px, -290px) rotateX(0deg) rotateY(0deg) scale(1)
        }

