  .swiper-button-prev,
  .swiper-rtl .swiper-button-next {
    color: #e6e6e6;
    width: 60px;
    height: 60px;
  }

  .swiper-button-prev,
  .swiper-rtl .swiper-button-next {
    left: -15px;
  }

  .swiper-button-next,
  .swiper-rtl .swiper-button-prev {
    right: -15px;
  }

  //스크롤바
