.best {
  width: 1420px;
  height: 600px;
  margin: 0 auto;
  overflow: hidden;

  h1 {
    font-size: 28px;
    overflow: hidden;
    text-align: center;
    font-weight: bold;
    margin-bottom: 50px;
  }

  .swiper-wrapper {
    position: relative;
    width: 1420px;
    margin: 0 auto;
    height: 100%;
    z-index: 1;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-transition-property: -webkit-transform;
    transition-property: -webkit-transform;
    transition-property: transform;
    transition-property: transform, -webkit-transform;
    box-sizing: content-box;

    .swiper-slide-visible.swiper-slide-active {
      position: relative;

      .best_text {
        position: absolute;
        bottom: 0;
        left: 14px;
        height: 75px;
        width: 450px;
        padding: 20px 25px;
        box-sizing: border-box;
        line-height: 20px;
        background-color: #fff;
        opacity: 0.6;
      }
    }

    .swiper-slide-visible.swiper-slide-next {
      transform: translate3d(-55px, -10px, -290px) rotateX(0deg) rotateY(0deg) scale(1) !important;

      .best_text {
        display: none;
      }
    }

    .swiper-slide-visible.swiper-slide-prev {
      transform: translate3d(55px, -10px, -290px) rotateX(0deg) rotateY(0deg) scale(1) !important;

      .best_text {
        display: none;
      }
    }

  }


  .swiper-container-android .swiper-slide,
  .swiper-wrapper {
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);

  }

  .swiper-container-multirow>.swiper-wrapper {
    -webkit-box-lines: multiple;
    -moz-box-lines: multiple;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap
  }

  .swiper-container-free-mode>.swiper-wrapper {
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
    margin: 0 auto
  }

  .swiper-slide {
    // -webkit-flex-shrink: 0;
    -ms-flex: 0 0 auto;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    width: 100%;
    height: 100%;
    position: relative;
  }

  .swiper-container-autoheight,
  .swiper-container-autoheight .swiper-slide {
    height: auto
  }

  .swiper-container .swiper-notification {
    position: absolute;
    left: 0;
    top: 0;
    pointer-events: none;
    opacity: 0;
    z-index: -1000;
  }

  .swiper-button-next,
  .swiper-rtl .swiper-button-prev,
  .swiper-button-prev,
  .swiper-rtl .swiper-button-next {
    position: absolute;
    color: #e6e6e6;
    width: 60px;
    height: 60px;
  }

  .swiper-rtl .swiper-button-prev,
  .swiper-button-prev {
    left: -10px;
  }

  .swiper-button-next,
  .swiper-rtl .swiper-button-next {
    right: -10px;
  }

  .swiper-container.one {
    padding-top: 40px;
    text-align: center;
  }

  .swiper-container.one .swiper-slide {
    //padding: 0 43px;
  }

  .swiper-container {
    width: 100%;
    padding-bottom: 60px;
  }

  .swiper-slide img {
    display: block;
    margin: auto;
    width: 100%;
  }

  .swiper-slide {
    width: 450px;
    height: 450px;
    padding: 0 15px;
  }

  .two .swiper-slide {
    width: 300px;
  }

}


