.instagram {
  width: 1420px;
  margin: 0 auto;
  margin-bottom: 110px;
  text-align: center;

  h1 {
    font-size: 28px;
    margin-bottom: 5px;
  }

  .hashtag {
    width: 1420px;
    text-align: center;
    margin-bottom: 40px;

    span {
      margin-right: 10px;
      color: #9e9e9e;
    }
  }

  a {
    p {
      font-size: 16px;
      margin-bottom: 70px;
    }

    h2 {
      font-size: 16px;
      color: #fd8234;
    }
  }

  .instagram_rolling {
    margin-bottom: 70px;
  }

  .instagram_wrap {
    display: flex;
    overflow: hidden;

    .rolling-list ul {
      display: flex;

      li {
        .image-wrap {
          //padding-right: 32px;
          width: 120px;
          height: 120px;
          flex-shrink: 0;
          margin-right: 10px;
        }

        .image-wrap a>img {
          width: 100%;
        }
      }
    }
  }

  .rolling-list.original {
    animation: rollingleft1 30s linear infinite;
  }

  .rolling-list.clone {
    animation: rollingleft2 30s linear infinite;
  }

  @keyframes rollingleft1 {
    0% {
      transform: translateX(0);
    }

    50% {
      transform: translateX(-100%);
    }

    50.01% {
      transform: translateX(100%);
    }

    100% {
      transform: translateX(0);
    }
  }

  @keyframes rollingleft2 {
    0% {
      transition: translateX(0);
    }

    100% {
      transform: translateX(-200%);
    }
  }
}



