            }

            li:hover {
              scale: 1.1;
            }

            li:nth-child(1) {
              background-color: rgb(231, 205, 157);

              a.btn {
                display: block;
                color: rgb(231, 205, 157);
                font-size: 13px;
              }
            }

            li:nth-child(2) {
              background-color: rgb(235, 198, 213);

              a.btn {
                display: block;
                color: rgb(235, 198, 213);
                font-size: 13px;
              }
            }

            li:nth-child(3) {
              background-color: white;
              margin-right: 20px;

              a.btn {
                display: block;
                color: white;
                font-size: 13px;
              }
            }

            p {
              font-size: 13px;
              font-weight: bold;
              line-height: 25px;
            }
          }
        }
      }

      .additional_product {
        height: 285px;
        overflow: hidden;
        border-bottom: 1px solid #e6e6e6;
        border-right: 1px solid #e6e6e6;
        box-sizing: border-box;
        padding: 30px 65px;

        p {
          font-size: 18px;
          width: 100%;
          margin-bottom: 13px;
        }

        .additional_wrap {
          &:hover img {
            border: 1px solid #e6e6e6;
            box-sizing: border-box;
          }

          &:hover a {
            text-decoration: underline;
          }

          width: 470px;
          height: 190px;

          .additional_image {
            float: left;
            width: 190px;
            height: 190px;
            margin-right: 20px;

            a {
              img {
                width: 100%;
              }
            }
          }

          .additional_text {
            float: left;
            width: calc(100% - 190px - 20px);
            height: 190px;
            padding-top: 130px;
            box-sizing: border-box;

            a {
              h1 {
                font-size: 18px;
                margin-bottom: 20px;
              }

              b {
                font-size: 18px;
                font-weight: bold;
              }
            }
          }
        }
      }

      .delivery_benefit {
        // position: relative;
        width: 600px;
        height: auto;
        padding: 20px 65px;
        border-bottom: 1px solid #e6e6e6;
        border-right: 1px solid #e6e6e6;
        box-sizing: border-box;
        font-size: 13px;

        div {
          display: flex;

          &:first-child {
            margin-bottom: 10px;
          }

          &:last-child {
            p {
              width: 20px;
              text-align: center;
              border: 2px solid #e6e6e6;
              border-radius: 8px;
            }
          }

          b {
            width: 55px;
            line-height: 20px;
            text-align: center;
            margin-right: 10px;
          }

          p {
            width: calc(100% - 55px);
            line-height: 20px;
          }
        }
      }

      .count {
        height: 85px;
        padding: 33px 65px;
        border-bottom: 1px solid #e6e6e6;
        border-right: 1px solid #e6e6e6;
        box-sizing: border-box;
        display: flex;

        form {
          // display: flex;
          width: 470px;

          .basketdiv {
            float: left;
            font-size: 18px;
            font-weight: bold;

            .row {
              position: relative;
              width: 340px;
              margin-right: 15px;

              // display: flex;
              .subdiv {
                // display: flex;
                float: left;

                &:nth-child(1) {
                  margin-right: 78px;
                }

                // num
                &:nth-child(2) {
                  position: absolute;
                  top: -12px;
                  right: 0;
                  width: 114px;

                  .updown {
                    position: relative;
                    border: 1px solid black;
                    box-sizing: border-box;
                    height: 39px;
                    box-sizing: border-box;

                    input {
                      position: absolute;
                      left: 35px;
                      width: 35px;
                      height: 35px;
                      top: -1px;
                    }

                    span {}

                    .minus {
                      position: relative;

                      i {
                        position: absolute;
                        top: 11px;
                        right: -25px;
                      }
                    }

                    .plus {
                      position: relative;

                      i {
                        position: absolute;
                        top: 11px;
                        right: -99px;
                      }
                    }
                  }
                }

                .check {
                  display: none;
                }

                .basketprice {
                  display: none;
                }

                .sum {
                  display: none;
                }

                .basketcmd {
                  display: none;
                }
              }
            }
          }
        }
      }

      .buy_cart {
        height: 85px;
        border-bottom: 1px solid #e6e6e6;
        border-right: 1px solid #e6e6e6;
        box-sizing: border-box;

        .buy,
        .cart {
          width: 50%;
          float: left;
          text-align: center;
          line-height: 85px;

          a {
            display: block;
          }
        }

        .buy {
          background-color: #efe8da;
        }

        .cart {
          background-color: #fff;
        }

        .buy:hover,
        .cart:hover {
          font-weight: bold;
        }
      }
    }

    .right_bottom {
      .accordion {
        background-color: #ffffff;
        cursor: pointer;
        padding: 35px;
        box-sizing: border-box;
        width: 600px;
        height: 85px;
        outline: none;
        border: none;
        text-align: left;
        font-size: 18px;
        transition: 0.4s;
        border-right: 1px solid #e6e6e6;

        &:after {
          content: "\f067";
          font-family: "Font Awesome 5 Free";
          font-weight: 900;
          margin-left: 5px;
          float: right;
        }

        &:hover {
          background-color: #f5f5f5;
          font-weight: bold;
        }
      }

      .active {
        background-color: #f5f5f5;
        font-weight: bold;

        &:after {
          content: "\f068";
          font-family: "Font Awesome 5 Free";
          font-weight: 900;
        }
      }

      .panel {
        background-color: #ffffff;
        max-height: 0;
        overflow: hidden;
        transition: max-height 0.2s ease-out;
        border-bottom: 1px solid #eeeeee;
        border-right: 1px solid #e6e6e6;
        box-sizing: border-box;

        p {
          font-size: 13px;
          line-height: 20px;
          font-weight: bold;
          margin: 27px;
          box-sizing: border-box;
        }
      }
    }
  }

  // 스크롤바 숨김
  .right_area::-webkit-scrollbar {
    display: none;
  }
}
